import React, { useState, useRef } from 'react';
import axios from 'axios';
import './form.css';
import * as html2pdf from 'html2pdf.js';


const ProgressBar = ({ activeFieldset }) => (
    <ul id="progressbar">
      <li className={activeFieldset === 1 ? 'active' : 'inactive'}>URL EXTRACTION</li>
      <li className={activeFieldset === 2 ? 'active' : 'inactive'}>SCRAPING</li>
      <li className={activeFieldset === 3 ? 'active' : 'inactive'}>EXTRACTED DATA</li>
    </ul>
  );

  
  const Fieldset = ({ title, subtitle, children, style }) => (
    <fieldset style={style}>
      <h2 className="fs-title">{title}</h2>
      <h3 className="fs-subtitle">{subtitle}</h3>
      {children}
    </fieldset>
  );

const ScraperForm = () => {

    const [activeFieldset, setActiveFieldset] = useState(1);
    const [formData, setFormData] = useState({
      email: '',
      text2: '',
      text3: '',
    });


  const [urls, setUrls] = useState('');
  const [url, setUrl] = useState('');
  const [selectors, setSelectors] = useState('');
  const [displayUrls, setDisplayUrls] = useState(false);
  const [output, setOutput] = useState('');
  const [outputs, setOutputs] = useState('');
  const [showUrlWarning, setShowUrlWarning] = useState(false);
  // const [downloadReady, setDownloadReady] = useState(false); 
  const preRef = useRef(null);


  const isUrlValid = (userInput) => {
    const res = userInput.match(/(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_+.~#?&//=]*)/g);
    return res !== null;
  };
  

  const handleDownloadPDF = () => {
    const element = preRef.current; 
    const pdfOptions = {
      margin: 10,
      filename: 'output.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'mm', format: 'a4', orientation: 'portrait' },
    };

    html2pdf(element, pdfOptions);
  };

  const downloadPDF = () => {
    const element = preRef.current; 
    const pdfOptions = {
      margin: 10,
      filename: 'output1.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'mm', format: 'a4', orientation: 'portrait' },
    };

    html2pdf(element, pdfOptions);
  };


  
  // const handleStart = async (e) => {
  //   e.preventDefault();
   
   
  //   try{
  //     const response = await axios.post('http://localhost:5051/scraper', { urls });
  //     setUrls(response.data.urls || []);
  //   } catch (error) {
  //     console.error('Error scraping URL:', error);
  //   }
  // }


  

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));

    if (name === 'email') {
      setShowUrlWarning(!isUrlValid(value));
      // const shortenedUrl = hashUrl(value);
      
      // setFormData((prevData) => ({
      //   ...prevData,
      //   [name]: shortenedUrl,
      // }));

      setShowUrlWarning(false);
    } else {
    
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }
  };

  const handleNext = () => {
    setActiveFieldset((prevActiveFieldset) => prevActiveFieldset + 1);
  };

  const handlePrevious = () => {
    setActiveFieldset((prevActiveFieldset) => prevActiveFieldset - 1);
  };




  const handleSubmit2 = async (e) => {
    e.preventDefault();

 
    try {
      const response = await axios.post('http://localhost:5050/scrape', {
        urls: urls.split(',').map((url) => url.trim()),
        selectors: selectors.split(',').map((selector) => selector.trim()),
      });

      console.log(response.data + '\n');

 
      setOutput(response.data.tableString);
      handleNext();

    } catch (error) {
      console.error('Error:', error);
    }
   
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try{
      const response = await axios.post('http://localhost:5000/scrape', { url });
      const scrapedUrls = response.data.urls || [];
      setUrls(scrapedUrls);  
      setDisplayUrls(true);
      setOutputs(response.data.urls);
    } catch (error) {
      console.error('Error scraping URL:', error);
    }
   
  };

  return (

  <div>
    <ProgressBar activeFieldset={activeFieldset} />
      <form id="msform" onSubmit={handleSubmit2}> 
        {/* <ProgressBar activeFieldset={activeFieldset} /> */}

        <Fieldset style={{ display: activeFieldset === 1 ? 'block' : 'none' }}>
        <h2 class="fs-title">Provide the Main URL</h2>
 		    <h3 class="fs-subtitle">This step will provide the list of all URLS</h3>
             <input
          type="text"
          name="email"
          placeholder="Main URL"
          value={formData.email}
          onChange={handleInputChange}
        />
        {showUrlWarning && <p style={{ color: 'red' }}>Please enter a valid URL.</p>}
        <div className="urlss">
          <pre ref={preRef}>{outputs}</pre>
        </div>
        <button type="button" className="next action-button" onClick={handleSubmit}>
          Start
        </button>
        <button type="button" className="next action-button"
          onClick={downloadPDF}
            disabled={!outputs.trim()}>
          Download
        </button>
        <button
          type="button"
          className="next action-button"
          onClick={handleNext}
          disabled={!formData.email.trim() || showUrlWarning}
        >
          Next
        </button>
        </Fieldset>



       <Fieldset style={{ display: activeFieldset === 2 ? 'block' : 'none' }}>
       <h2 className="fs-title">SCRAPING STAGE</h2>
        <h3 className="fs-subtitle">Enter the URL and the selector that you want to scrape</h3>
        <input
        type="text"
        value={urls}
        placeholder="Enter URL to scrape"
        onChange={(e) => {
          setUrls(e.target.value);
          setShowUrlWarning(!isUrlValid(e.target.value));
        }}
      />
        <input type="text" placeholder="Selectors" value={selectors} onChange={(e) => setSelectors(e.target.value)} />
        {showUrlWarning && <p style={{ color: 'red' }}>Please enter a valid URL.</p>}
        <button type="button" className="previous action-button" onClick={handlePrevious} >
          Previous
        </button>
        <button
        type="submit"
        className="submit action-button"
        onClick={handleSubmit2}
        disabled={!urls || !selectors || showUrlWarning}>
          Submit
        </button>
        </Fieldset> 

     
        <Fieldset style={{ display: activeFieldset === 3 ? 'block' : 'none' }}>
        <h2 className="fs-title">EXTRACTED DATA</h2>
        <div className="result">
          <pre ref={preRef}>{output}</pre>
        </div>
        <button type="button" className="previous action-button" onClick={handlePrevious}>
          Previous
        </button>
        <button
          type="button"
          className="next action-button"
          onClick={handleDownloadPDF}
            disabled={!output.trim()}
        >
          Download
        </button>
      </Fieldset>

      

    </form>
    </div>

  );
};

