import React, { useState } from 'react';
import axios from 'axios';
import './form.css';


const ProgressBar = ({ activeFieldset }) => (
    <ul id="progressbar">
      <li className={activeFieldset === 1 ? 'active' : ''}>URL EXTRACTION</li>
      <li className={activeFieldset === 2 ? 'active' : ''}>SCRAPING</li>
      <li className={activeFieldset === 3 ? 'active' : ''}>EXTRACTED DATA</li>
    </ul>
  );
  
  const Fieldset = ({ title, subtitle, children, style }) => (
    <fieldset style={style}>
      <h2 className="fs-title">{title}</h2>
      <h3 className="fs-subtitle">{subtitle}</h3>
      {children}
    </fieldset>
  );

const ScraperForm = () => {


    const [activeFieldset, setActiveFieldset] = useState(1);
  
    const [formData, setFormData] = useState({
      email: '',
      text2: '',
      text3: '',
    });


  const [urls, setUrls] = useState('');
  const [selectors, setSelectors] = useState('');
  const [output, setOutput] = useState('');

  const downloadPDF = async () => {
    try {
      const response = await fetch('http://localhost:5006/generate-pdf');
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'data.pdf');
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);
    } catch (error) {
      console.error('Error downloading PDF:', error);
    }
  };
  
  
  const handleStart = async (e) => {
    e.preventDefault();
   
   
    try{
      const response = await axios.post('http://localhost:5006/scrape', { urls });
      setUrls(response.data.urls || []);
    } catch (error) {
      console.error('Error scraping URL:', error);
    }
  }


  

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };
  



  const handleNext = () => {
    setActiveFieldset((prevActiveFieldset) => prevActiveFieldset + 1);
  };

  const handlePrevious = () => {
    setActiveFieldset((prevActiveFieldset) => prevActiveFieldset - 1);
  };




  const handleSubmit2 = async (e) => {
    e.preventDefault();
  if(urls && selectors){
    try {
      const response = await axios.post('http://localhost:5006/scrape', {
        urls: urls.split(',').map((url) => url.trim()),
        selectors: selectors.split(',').map((selector) => selector.trim()),
      });

      console.log(response.data + '\n');

      // Set the output in the state to be displayed on the frontend
      setOutput(response.data.tableString);
      handleNext();

    } catch (error) {
      console.error('Error:', error);
    }
  }
  else{
    setShowingWarning(true)
  }
  };

  return (

    <div>

      <form id="msform" onSubmit={handleSubmit2}> 
        <ProgressBar activeFieldset={activeFieldset} />

        <Fieldset style={{ display: activeFieldset === 1 ? 'block' : 'none' }}>
        <h2 class="fs-title">Provide the Main URL</h2>
 		    <h3 class="fs-subtitle">This step will provide the list of all URLS</h3>
         <input
  type="text"
  name="email"
  placeholder="Main URL"
  value={formData.email}
  onChange={handleInputChange}
/>

          <button type="button" className="next action-button" onClick={handleStart} >
            Start
          </button>
          
          <button type="button" className="next action-button" onClick={downloadPDF} >
            Download
          </button>
          <button type="button" className="next action-button" onClick={handleNext} disabled={!formData.email.trim()}>
            Next
          </button>
        </Fieldset>

       <Fieldset style={{ display: activeFieldset === 2 ? 'block' : 'none' }}>
       <h2 className="fs-title">SCRAPING STAGE</h2>
        <h3 className="fs-subtitle">Enter the URL and the selector that you want to scrape</h3>
        <input type="text" value={urls} placeholder="Enter URL to scrape" onChange={(e) => setUrls(e.target.value)} />
        <input type="text" placeholder="Selectors" value={selectors} onChange={(e) => setSelectors(e.target.value)} />
        {showWarning && <p style={{ color: 'red' }}>Please enter both URL and Selectors before submitting.</p>}
        <button type="button" className="previous action-button" onClick={handlePrevious} >
          Previous
        </button>
        <button type="submit" className="submit action-button" onClick={handleSubmit2}  disabled={!urls || !selectors}>
          Submit
        </button>
        </Fieldset> 

      
      <Fieldset  style={{ display: activeFieldset === 3 ? 'block' : 'none' }}>
      <h2 className="fs-title">EXTRACTED DATA</h2>
      <div className="result">
        <pre>{output}</pre>
      </div>

        <button type="button" className="previous action-button" onClick={handlePrevious}>
          Previous
        </button>
        
      </Fieldset>

      

    </form>
    </div>
    
     


  );
};

export default ScraperForm;




